package com.wxr.LCNum30;

import java.util.*;

/*串联所有单词的子串*/
/*Solution1 用了一个比较蠢的滑动窗口方法，处理速度极慢*/
/*自己的版本*/
public class Solution01 {

    public List<Integer> findSubstring(String s, String[] words) {

        List resultList = new ArrayList();

        /*简单的判错流程*/
        if (s.length() <= 0 || words.length <= 0) {
            return resultList;
        }

        /*正文
         * 解法，采用维护一个长度一定的滑动窗口来实现
         * */

        /*求最长length*/
        int maxLength = 0;
        int singleWord = 0;
        int sLen = s.length();
        int n = words.length;//数组长度，有多少个word

        /*原来想用hashset，但是发现，他不一定是不重复的*/
//        HashSet<String> wordSet = new HashSet<>();

        /*HashMap也不行 HashMap也是不重复的*/
//        HashMap<String,Integer> wordMap = new HashMap<>();
        for (String str : words) {
            maxLength += str.length();

        }
        singleWord = maxLength / n;//单个单词的长度

        int sliceStart = 0;//字符串切割的开始
        int sliceEnd = maxLength;//字符串切割的结束
        List<String> wordList = Arrays.asList(words);

        while (sliceEnd <= sLen) {
            String childStr = s.substring(sliceStart, sliceEnd);//切出一个子串
            List<String> tempList = new ArrayList<>();
            List<String> tempWordList = new ArrayList<>();
            tempWordList.addAll(wordList);
            //字串中分割
            for (int j = 0; j < n; j++) {
                tempList.add(childStr.substring(0 + singleWord * j, singleWord * (j + 1)));
            }

            boolean flag = true;

            for (String temp : tempList) {

                if (tempWordList.contains(temp)) {

                    tempWordList.set(tempWordList.indexOf(temp), "1");
                } else {
                    flag = false;
                }

            }
            if (flag) {
                resultList.add(sliceStart);
            }

            sliceEnd++;
            sliceStart++;
        }

        return resultList;

    }

    public static void main(String[] args) {
        Solution01 so1 = new Solution01();
        List a = so1.findSubstring("", new String[]{"ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba"});
        System.out.printf("a");

    }

}
